function increaseFontSize(){for(var a=document.getElementById("contentcell"),b=0;b<eleTypes.length;b++){var c=a.getElementsByTagName(eleTypes[b]);for(i=0;i<c.length;i++){if(c[i].style.fontSize)var d=parseInt(c[i].style.fontSize.replace("px",""));else var d=16;d!=max&&(d+=1),c[i].style.fontSize=d+"px"}}}function decreaseFontSize(){for(var a=document.getElementById("contentcell"),b=0;b<eleTypes.length;b++){var c=a.getElementsByTagName(eleTypes[b]);for(i=0;i<c.length;i++){if(c[i].style.fontSize)var d=parseInt(c[i].style.fontSize.replace("px",""));else var d=16;d!=min&&(d-=1),c[i].style.fontSize=d+"px"}}}var min=8,max=20,eleTypes=["p","a","td", "li"];